typedef struct {
  A_MPI_Copy_function *cp_function;
  A_MPI_Delete_function *del_function;
  int ref;
} myKeyval_functions_t;

typedef struct {
  int keyval;
  myKeyval_functions_t *functions;
  UT_hash_handle hh;
} myKeyval_translation_t;

pthread_mutex_t errhandler_locks = PTHREAD_MUTEX_INITIALIZER;

void errhandler_locks_ac(void) { pthread_mutex_lock(&errhandler_locks); }
void errhandler_locks_re(void) { pthread_mutex_unlock(&errhandler_locks); }
typedef struct {
  myKeyval_translation_t *table;
  pthread_mutex_t lock;
} myKeyval_translation_table_t;

myKeyval_translation_table_t myKeyval_table = {NULL, PTHREAD_MUTEX_INITIALIZER};

myKeyval_translation_table_t *get_myKeyval_table() { return &myKeyval_table; }

void myKeyval_read_lock() { pthread_mutex_lock(&get_myKeyval_table()->lock); }

void myKeyval_read_unlock() {
  pthread_mutex_unlock(&get_myKeyval_table()->lock);
}

void myKeyval_write_lock() { pthread_mutex_lock(&get_myKeyval_table()->lock); }

void myKeyval_write_unlock() {
  pthread_mutex_unlock(&get_myKeyval_table()->lock);
}
void myKeyval_translation_add(int keyval, myKeyval_functions_t *functions) {
  myKeyval_translation_t *conv;
  conv = malloc(sizeof(myKeyval_translation_t));
  conv->keyval = keyval;
  conv->functions = functions;
  myKeyval_write_lock();
  HASH_ADD_INT(get_myKeyval_table()->table, keyval, conv);
  HASH_FIND_INT(get_myKeyval_table()->table, &keyval, conv);
  assert(conv != NULL);
  print(stderr, "\t\tREGISTER keyval %d -> (%p, %p)\n", keyval,
        functions->cp_function, functions->del_function);
  myKeyval_write_unlock();
}

myKeyval_functions_t *myKeyval_translation_get(int keyval) {
  myKeyval_functions_t *functions;
  myKeyval_translation_t *conv;

  myKeyval_read_lock();
  HASH_FIND_INT(get_myKeyval_table()->table, &keyval, conv);
  if (conv != NULL) {
    functions = conv->functions;
    print(stderr, "\t\tkeyval %d -> (%p, %p) %d\n", keyval,
          functions->cp_function, functions->del_function, functions->ref);
  } else {
    myKeyval_read_unlock();

    return NULL;
  }
  myKeyval_read_unlock();
  return functions;
}
void myKeyval_translation_del(int keyval) {
  myKeyval_translation_t *conv;
  myKeyval_write_lock();
  HASH_FIND_INT(get_myKeyval_table()->table, &keyval, conv);
  assert(conv != NULL);
  HASH_DEL(get_myKeyval_table()->table, conv);
  // print(stderr, "\t\tDELETE keyval %d\n", keyval);
  myKeyval_write_unlock();
  free(conv);
}
int wrapper_copy_function(R_MPI_Comm comm, int keyval, void *extra_state,
                          void *attribute_val_in, void *attribute_val_out,
                          int *flag) {
  errhandler_locks_ac();
  A_MPI_Comm comm_tmp;
  comm_conv_r2a_static(&comm_tmp, &comm);
  myKeyval_functions_t *fns = myKeyval_translation_get(keyval);

  A_MPI_Copy_function *ptr_copy_func =
      (fns ? fns->cp_function : (A_MPI_Copy_function *)A_MPI_COMM_DUP_FN);
  if (fns)
    fns->ref++;
  //    printf("duped %d %p %d %p\n",
  //    keyval,comm,myKeyval_translation_get(keyval)->ref,ptr_copy_func);
  if (ptr_copy_func == (A_MPI_Copy_function *)A_MPI_NULL_COPY_FN ||
      ptr_copy_func == (A_MPI_Copy_function *)A_MPI_COMM_NULL_DELETE_FN) {
    *flag = 0;
    errhandler_locks_re();
    return R_MPI_SUCCESS;
  } else if (ptr_copy_func == (A_MPI_Copy_function *)A_MPI_COMM_DUP_FN) {
    *((void **)attribute_val_out) = attribute_val_in;
    *flag = 1;
    errhandler_locks_re();
    return R_MPI_SUCCESS;
  }
  int res = (ptr_copy_func)(comm_tmp, keyval, extra_state, attribute_val_in,
                            attribute_val_out, flag);
  // if copy is unsuccessful, should not have incremented ref counter
  if (res != R_MPI_SUCCESS && fns)
    fns->ref--;
  errhandler_locks_re();
  //    ptr_copy_func = NULL;
  return error_code_conv_r2a(res);
}

int wrapper_delete_function(R_MPI_Comm comm, int keyval, void *attribute_val,
                            void *extra_state) {
  errhandler_locks_ac();
  A_MPI_Comm comm_tmp;
  int res;
  comm_conv_r2a_static(&comm_tmp, &comm);
  myKeyval_functions_t *fns = myKeyval_translation_get(keyval);
  A_MPI_Delete_function *ptr_delete_func =
      fns == NULL ? A_MPI_NULL_DELETE_FN : fns->del_function;
  if (ptr_delete_func != A_MPI_NULL_DELETE_FN)
    res = (ptr_delete_func)(comm_tmp, keyval, attribute_val, extra_state);
  else
    res = R_MPI_SUCCESS;
  // Only decrement on success
  if (fns && res == R_MPI_SUCCESS) {
    fns->ref--;
    //    printf("deleted %d %p %d\n",
    //    keyval,comm,myKeyval_translation_get(keyval)->ref);
    if (fns->ref == 0)
      myKeyval_translation_del(keyval);
  } // ptr_delete_func = NULL;
  errhandler_locks_re();
  return error_code_conv_r2a(res);
}

